// StudentDashboard.js
import React, { useState, useEffect } from 'react';
import { db, auth } from '../firebaseConfig';
import { collection, getDocs } from 'firebase/firestore';
import { onAuthStateChanged } from 'firebase/auth'; // Removed unused 'signOut'
import { useNavigate } from 'react-router-dom';
import './StudentDashboard.css';

const StudentDashboard = ({ handleLogout }) => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Fetch the student's assigned projects
  const fetchProjects = async () => {
    try {
      const projectsQuery = collection(db, 'projects'); // Modify based on your Firestore structure
      const querySnapshot = await getDocs(projectsQuery);
      const fetchedProjects = querySnapshot.docs.map((doc) => doc.data());
      setProjects(fetchedProjects);
    } catch (error) {
      console.error("Error fetching projects:", error);
    }
  };

  useEffect(() => {
    fetchProjects();
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (!user) navigate('/');
    });
    return () => unsubscribe();
  }, [navigate]);

  const handleProjectClick = (projectId) => {
    navigate(`/project/${projectId}`);
  };

  return (
    <div className="student-dashboard">
      <div className="sidebar">
        <h2>Creative Assistant</h2>
        <button onClick={handleLogout}>Logout</button>
      </div>

      <div className="content">
        <h1>Your Projects</h1>
        <button className="home-button" onClick={() => navigate('/')}>Home</button>

        {projects.length > 0 ? (
          projects.map((project) => (
            <button
              key={project.id}
              onClick={() => handleProjectClick(project.id)}
              className="project-button"
            >
              {project.name}
            </button>
          ))
        ) : (
          <p className="no-projects">No projects available.</p>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
