import React, { useState } from 'react';
import XLSX from 'xlsx';
import { storage, db } from '../firebaseConfig'; // Firebase storage and db
import { getFirestore, collection, doc, setDoc } from 'firebase/firestore';

const TeacherDashboard = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Handle file upload
  const handleFileUpload = (e) => {
    const file = e.target.files[0];

    if (!file) {
      alert("No file selected");
      return;
    }

    setLoading(true);
    const reader = new FileReader();

    // Handle file reading
    reader.onload = async (event) => {
      try {
        // Read the contents of the file
        const fileData = event.target.result;
        const workbook = XLSX.read(fileData, { type: 'binary' });

        // Assuming the data is in the first sheet
        const sheet = workbook.Sheets[workbook.SheetNames[0]];
        const jsonData = XLSX.utils.sheet_to_json(sheet);

        // Loop through the rows and process each row
        for (const row of jsonData) {
          const { 'Student Email': email, 'Project Name': projectName, 'Team Name': teamName } = row;

          // Check if the required columns are present
          if (!email || !projectName || !teamName) {
            setError("Missing required fields in the file.");
            setLoading(false);
            return;
          }

          // Check if the student exists in Firestore
          const studentRef = doc(db, "users", email); // Assuming 'users' collection stores student data
          const docSnap = await getDoc(studentRef);

          if (docSnap.exists()) {
            // If student exists, assign the project and team
            const studentData = docSnap.data();
            const studentProjectsRef = collection(db, "projects"); // Adjust this collection based on your data structure

            // Add or update the student's assignment
            await setDoc(doc(studentProjectsRef, `${email}-${projectName}`), {
              projectName,
              teamName,
              email,
              assignedAt: new Date()
            });
          } else {
            console.log(`Student with email ${email} not found`);
          }
        }

        alert('File uploaded and students assigned successfully.');
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError("An error occurred while processing the file.");
        setLoading(false);
      }
    };

    // Read the file as binary string
    reader.readAsBinaryString(file);
  };

  return (
    <div>
      <h1>Teacher Dashboard</h1>

      {/* Upload file input */}
      <input type="file" accept=".xlsx,.csv" onChange={handleFileUpload} />

      {/* Loading/Error States */}
      {loading && <p>Uploading...</p>}
      {error && <p>{error}</p>}
    </div>
  );
};

export default TeacherDashboard;
