import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script';
import { Routes, Route, useNavigate } from 'react-router-dom';
import './App.css';
import TeacherDashboard from './components/TeacherDashboard';
import StudentDashboard from './components/StudentDashboard';
import ProjectPage from './components/ProjectDetails.js'; // Import the new ProjectPage component
import ClassroomDetails from './components/ClassroomDetails'; // New component for classroom details
import ManageClassroom from './components/ManageClassroom'; // New component for classroom management
import Projects from './components/Projects'; // New component for project management
import { initializeGoogleAuth } from './utils/auth'; // Google Auth Initialization
import { getFirestore, collection, query, where, getDocs } from "firebase/firestore"; 
import { db } from './firebaseConfig'; // Import Firebase Firestore

function App() {
  const [role, setRole] = useState(null); // Role state
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Login state
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(null); // Error state
  const navigate = useNavigate(); // To navigate after logout or login

  // Initialize Google Auth once when the component mounts
  useEffect(() => {
    initializeGoogleAuth();
  }, []);

  // Google login handler
  const handleGoogleLogin = () => {
    setLoading(true); // Start loading state
    const authInstance = gapi.auth2.getAuthInstance();
    if (!authInstance) {
      console.error('Google Auth instance is not initialized');
      setLoading(false);
      return;
    }

    // Sign in with Google and get user data
    authInstance.signIn()
      .then(async (googleUser) => {
        setLoading(false); // Stop loading after successful login
        const profile = googleUser.getBasicProfile();
        const email = profile.getEmail();
        console.log("Logged in as:", email);

        // Check Firestore for the user's role using the email field
        const usersCollection = collection(db, "users");
        const q = query(usersCollection, where("email", "==", email));
        const querySnapshot = await getDocs(q);

        if (!querySnapshot.empty) {
          querySnapshot.forEach((doc) => {
            const userData = doc.data();
            console.log("User role:", userData.role); // Log the role from Firestore
            setRole(userData.role); // Set the role based on Firestore document
            setIsLoggedIn(true); // Set logged-in state to true
            navigate('/'); // Redirect to the homepage after login
          });
        } else {
          console.log("Email not found in Firestore");
          setRole(null); // Email not found in Firestore
          alert('Email is not registered. Please contact the admin.');
        }
      })
      .catch((error) => {
        setLoading(false); // Stop loading on error
        setError('Login failed, please try again.');
        console.error('Google login failed', error);
      });
  };

  // Handle logout
  const handleLogout = () => {
    const authInstance = gapi.auth2.getAuthInstance();
    authInstance.signOut().then(() => {
      setIsLoggedIn(false);
      setRole(null);
      navigate('/'); // Redirect to home page after logout
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Creative Assistant</h1>

        {/* Conditional rendering based on login and role */}
        {isLoggedIn ? (
          <Routes>
            {/* Render the dashboard based on the role */}
            {role === 'teacher' && (
              <>
                <Route path="/" element={<TeacherDashboard handleLogout={handleLogout} />} />
                <Route path="/classroom/:classroomId" element={<ClassroomDetails />} />
                <Route path="/classroom/:classroomId/manage" element={<ManageClassroom />} />
                <Route path="/classroom/:classroomId/projects" element={<Projects />} />
                <Route path="/classroom/:classroomId/projects/:projectName" element={<ProjectPage />} />
              </>
            )}
            {role === 'student' && (
              <Route path="/" element={<StudentDashboard handleLogout={handleLogout} />} />
            )}
            {role === null && <p>Email is not registered. Please contact the admin.</p>}
          </Routes>
        ) : (
          <>
            {error && <p className="error">{error}</p>}
            {loading ? <p>Loading...</p> : (
              <>
                <button onClick={handleGoogleLogin}>Login with Google</button>
              </>
            )}
          </>
        )}
      </header>
      <footer className="footer">
        <p>Creative Assistant &copy; 2024</p>
      </footer>
    </div>
  );
}

export default App;
