import React, { useState, useEffect } from 'react';
import { db, storage } from '../firebaseConfig'; // Import Firebase configuration
import { collection, getDocs, setDoc, doc, getDoc } from 'firebase/firestore';
import { ref, uploadBytes } from 'firebase/storage'; // Import Firebase storage functions
import { useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx'; // To read the Excel file
import { v4 as uuidv4 } from 'uuid'; // For generating unique IDs
import './TeacherDashboard.css';

const TeacherDashboard = () => {
  const [classrooms, setClassrooms] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [courseID, setCourseID] = useState('');
  const [file, setFile] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch classrooms from Firebase
    const fetchClassrooms = async () => {
      const classroomsQuery = collection(db, 'classrooms');
      const querySnapshot = await getDocs(classroomsQuery);
      const fetchedClassrooms = querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setClassrooms(fetchedClassrooms);
    };

    fetchClassrooms();
  }, []);

  const handleClassroomClick = (classroomId) => {
    navigate(`/classroom/${classroomId}`);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleCourseIDChange = (e) => {
    setCourseID(e.target.value);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!courseID || !file) {
      setErrorMessage('Please fill in both the course ID and upload a file.');
      return;
    }

    try {
      // Fetch teacher's email (e.g., from Google Auth)
      const authInstance = gapi.auth2.getAuthInstance();
      const googleUser = authInstance.currentUser.get();
      const profile = googleUser.getBasicProfile();
      const teacherEmail = profile.getEmail();

      // Check if classroom exists, if not create a new one
      const classroomRef = doc(db, 'classrooms', courseID);
      const classroomSnap = await getDoc(classroomRef);

      if (!classroomSnap.exists()) {
        // Create new classroom document in Firestore
        await setDoc(classroomRef, {
          classID: courseID,
          teacherEmail, // Store the teacher's email for reference
        });
        console.log(`Classroom ${courseID} created.`);
      } else {
        console.log(`Classroom ${courseID} already exists.`);
      }

      // Upload the file to Firebase storage
      const storageRef = ref(storage, `classrooms/${courseID}/${file.name}`);
      await uploadBytes(storageRef, file);

      // Process the file and upload student data
      const fileReader = new FileReader();
      fileReader.onload = async () => {
        try {
          const fileData = fileReader.result;
          const workbook = XLSX.read(fileData, { type: 'binary' });
          const sheet = workbook.Sheets[workbook.SheetNames[0]];
          const jsonData = XLSX.utils.sheet_to_json(sheet);

          // Process student data and store them in Firestore
          for (const row of jsonData) {
            const { 'Student Email': email, 'First name': firstName, 'Last name': lastName } = row;

            if (!email || !firstName || !lastName) {
              console.log("Skipping incomplete row:", row);
              continue; // Skip incomplete rows
            }

            // Add student to the classroom
            const studentRef = doc(collection(db, 'classrooms', courseID, 'students'), email);
            await setDoc(studentRef, {
              email,
              firstName,
              lastName,
            });
            console.log(`Added student: ${firstName} ${lastName}`);
          }

          // Close modal and refresh classrooms list
          setIsModalOpen(false);
          setCourseID('');
          setFile(null);
          setErrorMessage('');

          // Fetch updated classrooms
          const classroomsQuery = collection(db, 'classrooms');
          const querySnapshot = await getDocs(classroomsQuery);
          const fetchedClassrooms = querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
          setClassrooms(fetchedClassrooms);
        } catch (error) {
          console.error("Error processing file:", error);
          setErrorMessage("Error processing student data. Please try again.");
        }
      };

      fileReader.readAsBinaryString(file);

    } catch (error) {
      console.error('Error uploading classroom data:', error);
      setErrorMessage('Failed to upload data. Please try again.');
    }
  };

  return (
    <div className="teacher-dashboard">
      <h1>Your Classrooms</h1>

      {/* Plus button to add a new classroom */}
      <button className="plus-button" onClick={() => setIsModalOpen(true)}>
        +
      </button>

      {/* Display list of classrooms */}
      {classrooms.length > 0 ? (
        classrooms.map((classroom) => (
          <button
            key={classroom.id}
            onClick={() => handleClassroomClick(classroom.id)}
            className="classroom-button"
          >
            {classroom.classID}
          </button>
        ))
      ) : (
        <p>No classrooms available.</p>
      )}

      {/* Modal for adding new classroom */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setIsModalOpen(false)}>
              &times;
            </span>
            <h2>Create New Classroom</h2>
            <form onSubmit={handleFormSubmit}>
              <div>
                <label>Course ID:</label>
                <input
                  type="text"
                  value={courseID}
                  onChange={handleCourseIDChange}
                  placeholder="Enter course ID"
                />
              </div>
              <div>
                <label>Upload Students (CSV/Excel):</label>
                <input
                  type="file"
                  accept=".csv, .xlsx"
                  onChange={handleFileChange}
                />
              </div>
              {errorMessage && <p className="error">{errorMessage}</p>}
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherDashboard;
