import React, { useEffect, useState } from 'react';
import { db } from '../firebaseConfig'; // Assuming Firebase is configured
import { doc, getDoc } from 'firebase/firestore';

const StudentDashboard = () => {
  const [assignment, setAssignment] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAssignment = async () => {
      const user = firebase.auth().currentUser;
      if (user) {
        const assignmentRef = doc(db, "assignments", user.email);
        const docSnap = await getDoc(assignmentRef);

        if (docSnap.exists()) {
          setAssignment(docSnap.data());
        } else {
          setAssignment(null);
        }
      } else {
        setAssignment(null);
      }
      setLoading(false);
    };

    fetchAssignment();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Your Assignment</h2>
      {assignment ? (
        <p>You are assigned to project: {assignment.project}</p>
      ) : (
        <p>You have no assigned project yet.</p>
      )}
    </div>
  );
};

export default StudentDashboard;
