import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { db } from '../firebaseConfig';
import { setDoc, doc, collection, getDoc } from 'firebase/firestore';

const TeacherDashboard = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [classroomName, setClassroomName] = useState('');

  // Handle file upload for creating a classroom and assigning students
  const handleClassroomFileUpload = (e) => {
    const file = e.target.files[0];

    if (!file) {
      alert("No file selected");
      return;
    }

    if (!classroomName) {
      alert("Please enter a classroom name");
      return;
    }

    setLoading(true);
    const reader = new FileReader();

    reader.onload = async (event) => {
      try {
        const fileData = event.target.result;
        const workbook = XLSX.read(fileData, { type: 'binary' });
        const sheet = workbook.Sheets[workbook.SheetNames[0]];
        const jsonData = XLSX.utils.sheet_to_json(sheet);

        // Debug: Check loaded JSON data
        console.log("Loaded JSON data:", jsonData);

        // Check if classroom exists, if not create a new one
        const classroomRef = doc(db, 'classrooms', classroomName);
        const classroomSnap = await getDoc(classroomRef);

        // If the classroom doesn't exist, create it
        if (!classroomSnap.exists()) {
          await setDoc(classroomRef, { classroomName, courseID: classroomName.toUpperCase() });
          console.log(`Classroom ${classroomName} created.`);
        }

        // Process each row to create student entries under the classroom's "students" sub-collection
        for (const row of jsonData) {
          const { 'Student Email': email, 'First name': firstName, 'Last name': lastName } = row;

          if (!email || !firstName || !lastName) {
            setError("Missing required fields in the file.");
            setLoading(false);
            return;
          }

          const studentRef = doc(collection(classroomRef, 'students'), email); // Student document by email
          await setDoc(studentRef, {
            email,
            firstName,
            lastName,
            assignedAt: new Date().toISOString(), // Store the current timestamp
          });
          console.log(`Student ${firstName} ${lastName} added to classroom ${classroomName}`);
        }

        alert('Classroom created and students added successfully.');
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError("An error occurred while processing the file.");
        setLoading(false);
      }
    };

    reader.readAsBinaryString(file);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Teacher Dashboard</h1>

      {/* Classroom name input */}
      <label>
        Classroom Name:
        <input 
          type="text" 
          value={classroomName} 
          onChange={(e) => setClassroomName(e.target.value)} 
          style={{ marginLeft: '10px', padding: '5px' }}
        />
      </label>

      {/* Upload file input */}
      <input 
        type="file" 
        accept=".xlsx,.csv" 
        onChange={handleClassroomFileUpload} 
        style={{ marginTop: '10px', padding: '5px' }} 
      />

      {/* Loading/Error States */}
      {loading && <p style={{ color: 'blue' }}>Uploading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default TeacherDashboard;
