import React, { useEffect } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import { auth } from '../firebaseConfig';
import { signOut } from 'firebase/auth';
import './Layout.css';
import logo from '../assets/lsu-logo.png'; // Import logo image

const Layout = () => {
  const navigate = useNavigate();

  // Check if the user is logged out (optionally handle user state)
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (!user) {
        navigate('/'); // Redirect to login page if no user is authenticated
      }
    });

    return () => unsubscribe(); // Cleanup the listener on component unmount
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await signOut(auth);  // Sign out the user from Firebase
      navigate('/'); // Redirect to login page after logout
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div className="layout">
      <aside className="sidebar">
        <img src={logo} alt="LSU Logo" className="logo" /> {/* LSU Logo */}
        <h2>Creative Assistant</h2>
        <nav>
          <Link to="/TeacherDashboard">Teacher Dashboard</Link> {/* Link to teacher dashboard */}
          <button onClick={handleLogout}>Logout</button> {/* Logout button */}
        </nav>
      </aside>
      <main className="content">
        <Outlet /> {/* This renders the nested route content */}
      </main>
    </div>
  );
};

export default Layout;
