// src/App.js
import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script';
import './App.css';
import TeacherDashboard from './components/TeacherDashboard';
import StudentDashboard from './components/StudentDashboard';
import { initializeGoogleAuth } from './utils/auth'; // Google Auth Initialization
import { getFirestore, doc, getDoc } from "firebase/firestore"; 
import { db } from './firebaseConfig'; // Import Firebase Firestore

function App() {
  const [role, setRole] = useState(null); // Role state
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Login state
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(null); // Error state

  // Initialize Google Auth once when the component mounts
  useEffect(() => {
    initializeGoogleAuth();
  }, []);

  // Google login handler
  const handleGoogleLogin = () => {
    setLoading(true); // Start loading state
    const authInstance = gapi.auth2.getAuthInstance();
    if (!authInstance) {
      console.error('Google Auth instance is not initialized');
      setLoading(false);
      return;
    }

    // Sign in with Google and get user data
    authInstance.signIn()
      .then(async (googleUser) => {
        setLoading(false); // Stop loading after successful login
        const profile = googleUser.getBasicProfile();
        console.log("Logged in as:", profile.getEmail());

        // Check Firestore for the user's role
        const userRef = doc(db, "users", profile.getEmail()); // Firestore document reference
        const docSnap = await getDoc(userRef);

        if (docSnap.exists()) {
          const userData = docSnap.data();
          console.log("User role:", userData.role); // Log the role from Firestore
          setRole(userData.role); // Set the role based on Firestore document
          setIsLoggedIn(true); // Set logged-in state to true
        } else {
          console.log("Email not found in Firestore");
          setRole(null); // Email not found in Firestore
          alert('Email is not registered. Please contact the admin.');
        }
      })
      .catch((error) => {
        setLoading(false); // Stop loading on error
        setError('Login failed, please try again.');
        console.error('Google login failed', error);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Creative Assistant</h1>

        {/* Conditional rendering based on login and role */}
        {isLoggedIn ? (
          <>
            {/* Render the dashboard based on the role */}
            {role === 'teacher' && <TeacherDashboard />}
            {role === 'student' && <StudentDashboard />}
            {role === null && <p>Email is not registered. Please contact the admin.</p>}
          </>
        ) : (
          <>
            {error && <p className="error">{error}</p>}
            {loading ? <p>Loading...</p> : (
              <>
                <button onClick={handleGoogleLogin}>Login with Google</button>
              </>
            )}
          </>
        )}
      </header>
      <footer className="footer">
        <p>Creative Assistant &copy; 2024</p>
      </footer>
    </div>
  );
}

export default App;
