import React, { useState } from 'react';
import { addUserWithRole } from '../utils/firestoreHelpers';

function SignUpForm({ role }) {
  const [formData, setFormData] = useState({
    courseCode: '',
    name: '',
    email: '',
    studentId: '',
    firstName: '',
    lastName: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { email } = formData;

    if (!email) {
      alert('Email is required');
      return;
    }

    try {
      // Call Firestore function to add user with role
      await addUserWithRole(email, role);
      alert(`Signed up as ${role} with email: ${email}`);
    } catch (error) {
      console.error('Error signing up:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {role === 'student' ? (
        <>
          <label>Course Entry Code *</label>
          <input
            type="text"
            name="courseCode"
            placeholder="Enter your six-character course entry code"
            value={formData.courseCode}
            onChange={handleInputChange}
            required
          />
          <label>Name *</label>
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
          <label>Email Address *</label>
          <input
            type="email"
            name="email"
            placeholder="email@example.com"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
          <label>Student ID *</label>
          <input
            type="text"
            name="studentId"
            placeholder="Your Student ID"
            value={formData.studentId}
            onChange={handleInputChange}
            required
          />
          <button type="submit">Sign Up as Student</button>
        </>
      ) : (
        <>
          <label>First Name *</label>
          <input
            type="text"
            name="firstName"
            placeholder="Your First Name"
            value={formData.firstName}
            onChange={handleInputChange}
            required
          />
          <label>Last Name *</label>
          <input
            type="text"
            name="lastName"
            placeholder="Your Last Name"
            value={formData.lastName}
            onChange={handleInputChange}
            required
          />
          <label>LSU Email Address *</label>
          <input
            type="email"
            name="email"
            placeholder="email@lsu.edu"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
          <button type="submit">Sign Up as Teacher</button>
        </>
      )}
    </form>
  );
}

export default SignUpForm;
