import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script';
import { useNavigate } from 'react-router-dom';
import './App.css';
import TeacherDashboard from './components/TeacherDashboard';
import StudentDashboard from './components/StudentDashboard';
import { initializeGoogleAuth } from './utils/auth';

function App() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);

  useEffect(() => {
    initializeGoogleAuth();
  }, []);

  // Google login handler
  const handleGoogleLogin = () => {
    setLoading(true);
    const authInstance = gapi.auth2.getAuthInstance();
    authInstance.signIn()
      .then((googleUser) => {
        setIsLoggedIn(true);
        setLoading(false);
        const profile = googleUser.getBasicProfile();
        setUser({
          id: profile.getId(),
          name: profile.getName(),
          email: profile.getEmail(),
        });

        // Directly assign the role after login (you can adjust the logic here)
        if (profile.getEmail().endsWith('@lsu.edu')) {
          setRole('teacher'); // If email matches a teacher's domain
        } else {
          setRole('student'); // Default to student role
        }
      })
      .catch((error) => {
        setLoading(false);
        setError('Login failed, please try again.');
        console.error('Google login failed', error);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Creative Assistant</h1>
        {isLoggedIn ? (
          <>
            <div className="dashboard-buttons">
              <button onClick={() => setRole('teacher')}>Go to Teacher Dashboard</button>
              <button onClick={() => setRole('student')}>Go to Student Dashboard</button>
            </div>
            {/* Render the dashboard based on selected role */}
            {role === 'teacher' && <TeacherDashboard />}
            {role === 'student' && <StudentDashboard />}
          </>
        ) : (
          <>
            {error && <p className="error">{error}</p>}
            {loading ? <p>Loading...</p> : (
              <>
                <button onClick={handleGoogleLogin}>Login with Google</button>
              </>
            )}
          </>
        )}
      </header>
      <footer className="footer">
        <p>Creative Assistant &copy; 2024</p>
      </footer>
    </div>
  );
}

export default App;
