import React from 'react';

function RoleSelection({ setRole, setShowSignUpOptions }) {
  return (
    <div className="popup-container">
      <div className="popup">
        <h3>Sign Up</h3>
        <button onClick={() => { setRole('student'); setShowSignUpOptions(false); }}>Sign Up as Student</button>
        <button onClick={() => { setRole('teacher'); setShowSignUpOptions(false); }}>Sign Up as Teacher</button>
        <button onClick={() => setShowSignUpOptions(false)}>Cancel</button>
      </div>
    </div>
  );
}

export default RoleSelection;
