import React, { useState } from 'react';
import { db } from '../utils/firebase'; // Import your Firestore instance

function SignUpForm({ role }) {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [studentID, setStudentID] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (role === 'student' && !studentID) {
      setError('Student ID is required');
      return;
    }

    // Create user object
    const user = {
      email,
      name,
      role,
      studentID: role === 'student' ? studentID : null,
      createdAt: new Date(),
    };

    try {
      // Add user data to Firestore
      await db.collection('users').doc(email).set(user);

      // After successful signup, navigate or give feedback
      alert(`Successfully signed up as ${role}`);
    } catch (err) {
      setError('Error signing up. Please try again.');
      console.error(err);
    }
  };

  if (role === 'student') {
    return (
      <form onSubmit={handleSubmit}>
        <label>Name *</label>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <label>Email Address *</label>
        <input
          type="email"
          placeholder="email@example.com"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <label>Student ID *</label>
        <input
          type="text"
          placeholder="Your Student ID"
          value={studentID}
          onChange={(e) => setStudentID(e.target.value)}
          required
        />
        <button type="submit">Sign Up as Student</button>
        {error && <p>{error}</p>}
      </form>
    );
  } else if (role === 'teacher') {
    return (
      <form onSubmit={handleSubmit}>
        <label>Name *</label>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <label>Email Address *</label>
        <input
          type="email"
          placeholder="email@example.com"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">Sign Up as Teacher</button>
        {error && <p>{error}</p>}
      </form>
    );
  }

  return null;
}

export default SignUpForm;
