import React, { useState, useEffect } from 'react';
import { db } from '../firebaseConfig';
import { collection, setDoc, doc, getDocs } from 'firebase/firestore';
import { signOut } from 'firebase/auth';
import { auth } from '../firebaseConfig';
import { useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import './TeacherDashboard.css';
import { onAuthStateChanged } from 'firebase/auth';

const TeacherDashboard = () => {
  const [classrooms, setClassrooms] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [courseID, setCourseID] = useState('');
  const [file, setFile] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [user, setUser] = useState(null); // Track user state
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch classrooms from Firebase
    const fetchClassrooms = async () => {
      const classroomsQuery = collection(db, 'classrooms');
      const querySnapshot = await getDocs(classroomsQuery);
      const fetchedClassrooms = querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setClassrooms(fetchedClassrooms);
    };

    fetchClassrooms();

    // Track auth state and redirect if necessary
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUser(user); // Set user state to track login status
      if (!user) {
        navigate('/'); // Redirect to the home page if user is logged out
      }
    });

    return () => unsubscribe(); // Cleanup on unmount
  }, [navigate]);

  const handleClassroomClick = (classroomId) => {
    navigate(`/classroom/${classroomId}`);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleCourseIDChange = (e) => {
    setCourseID(e.target.value);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!courseID || !file) {
      setErrorMessage('Please fill in both the course ID and upload a file.');
      return;
    }

    setLoading(true);

    try {
      const classroomDocRef = doc(db, 'classrooms', courseID);

      // Process the file and upload student data
      const fileReader = new FileReader();
      fileReader.onload = async () => {
        try {
          const fileData = fileReader.result;
          const workbook = XLSX.read(fileData, { type: 'array' });
          const sheet = workbook.Sheets[workbook.SheetNames[0]];
          const jsonData = XLSX.utils.sheet_to_json(sheet);

          // Create classroom document in Firestore
          await setDoc(classroomDocRef, {
            classID: courseID,
          });

          // Process student data and store them in Firestore
          for (const row of jsonData) {
            const { 'Student Email': email, 'First name': firstName, 'Last name': lastName } = row;

            if (!email || !firstName || !lastName) {
              console.log("Skipping incomplete row:", row);
              continue;
            }

            // Add student to the classroom's students subcollection
            const studentRef = doc(collection(db, 'classrooms', courseID, 'students'), email);
            await setDoc(studentRef, {
              firstName,
              lastName,
              email,
              assignedAt: new Date().toISOString(),
            });
          }

          // Fetch updated classrooms
          const classroomsQuery = collection(db, 'classrooms');
          const querySnapshot = await getDocs(classroomsQuery);
          const fetchedClassrooms = querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
          setClassrooms(fetchedClassrooms);

          // Close modal and reset form
          setIsModalOpen(false);
          setCourseID('');
          setFile(null);
          setErrorMessage('');
          setLoading(false);
        } catch (error) {
          setErrorMessage("Error processing student data. Please try again.");
          setLoading(false);
        }
      };

      fileReader.readAsArrayBuffer(file);

    } catch (error) {
      setErrorMessage('Failed to upload data. Please try again.');
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      // Log out the user from Firebase
      await signOut(auth);  
      console.log('Logged out successfully');

      // Redirect to the home page
      navigate('/'); // Ensure navigate() is called after signOut
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div className="teacher-dashboard">
      <div className="sidebar">
        <h2>Creative Assistant</h2>
        <button onClick={handleLogout}>Logout</button>
      </div>

      <div className="content">
        <h1>Your Classrooms</h1>
        <button className="plus-button" onClick={() => setIsModalOpen(true)}>
          +
        </button>

        {classrooms.length > 0 ? (
          classrooms.map((classroom) => (
            <button
              key={classroom.id}
              onClick={() => handleClassroomClick(classroom.id)}
              className="classroom-button"
            >
              {classroom.classID}
            </button>
          ))
        ) : (
          <p>No classrooms available.</p>
        )}

        {isModalOpen && (
          <div className="modal">
            <div className="modal-content">
              <span className="close" onClick={() => setIsModalOpen(false)}>
                &times;
              </span>
              <h2>Create New Classroom</h2>
              <form onSubmit={handleFormSubmit}>
                <div>
                  <label>Course ID:</label>
                  <input
                    type="text"
                    value={courseID}
                    onChange={handleCourseIDChange}
                    placeholder="Enter course ID"
                  />
                </div>
                <div>
                  <label>Upload Students (CSV/Excel):</label>
                  <input
                    type="file"
                    accept=".csv, .xlsx"
                    onChange={handleFileChange}
                  />
                </div>
                {errorMessage && <p className="error">{errorMessage}</p>}
                <button type="submit" disabled={loading}>
                  {loading ? 'Loading...' : 'Submit'}
                </button>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TeacherDashboard;
