import React, { useEffect, useState } from 'react';
import { db } from '../firebaseConfig';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';
import { gapi } from 'gapi-script';
import './StudentDashboard.css';

const StudentDashboard = () => {
  const [classrooms, setClassrooms] = useState([]); // State to hold classroom data
  const [selectedClassroom, setSelectedClassroom] = useState(null); // State to track selected classroom
  const [assignments, setAssignments] = useState([]); // State to hold assignments for selected classroom
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClassrooms = async () => {
      setLoading(true);
      try {
        // Get the current user's email from Google Auth
        const authInstance = gapi.auth2.getAuthInstance();
        const googleUser = authInstance.currentUser.get();
        const profile = googleUser.getBasicProfile();
        const userEmail = profile.getEmail();

        // Fetch classrooms for the student based on their email
        const classroomsCollection = collection(db, 'classrooms');
        const q = query(classroomsCollection, where("students.email", "array-contains", userEmail));
        const querySnapshot = await getDocs(q);

        const classroomsData = [];
        querySnapshot.forEach((doc) => {
          classroomsData.push({
            id: doc.id,
            classroomName: doc.data().classroomName,
          });
        });

        setClassrooms(classroomsData); // Store classrooms in state
      } catch (error) {
        console.error("Error fetching classrooms:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchClassrooms();
  }, []);

  const handleClassroomClick = async (classroomId) => {
    setLoading(true);
    setSelectedClassroom(classroomId); // Set the selected classroom

    try {
      // Fetch the assignments for the selected classroom
      const assignmentsCollection = collection(db, 'classrooms', classroomId, 'assignments');
      const querySnapshot = await getDocs(assignmentsCollection);

      const assignmentsData = [];
      querySnapshot.forEach((doc) => {
        assignmentsData.push(doc.data());
      });

      setAssignments(assignmentsData); // Set the assignments for the selected classroom
    } catch (error) {
      console.error("Error fetching assignments:", error);
    } finally {
      setLoading(false);
    }
  };

  const openProjectPage = (projectName) => {
    navigate(`/project/${projectName}`);
  };

  return (
    <div className="student-dashboard">
      <h2>Student Dashboard</h2>

      {loading ? (
        <p>Loading your classrooms...</p>
      ) : classrooms.length > 0 ? (
        <div className="classroom-list">
          <h3>Your Classrooms</h3>
          {classrooms.map((classroom) => (
            <button
              key={classroom.id}
              onClick={() => handleClassroomClick(classroom.id)}
              className="classroom-button"
            >
              {classroom.classroomName}
            </button>
          ))}
        </div>
      ) : (
        <p>No classrooms found.</p>
      )}

      {selectedClassroom && assignments.length > 0 && (
        <div className="assignments-list">
          <h3>Assignments in {selectedClassroom}</h3>
          {assignments.map((assignment, index) => (
            <div key={index} className="assignment-card">
              <h4>{assignment.projectName}</h4>
              <button onClick={() => openProjectPage(assignment.projectName)} className="project-button">
                View Project
              </button>
              <p><strong>Due Date:</strong> {assignment.dueDate}</p>
              <p><strong>Status:</strong> {assignment.status}</p>
            </div>
          ))}
        </div>
      )}

      {selectedClassroom && assignments.length === 0 && (
        <p>No assignments found for this classroom.</p>
      )}
    </div>
  );
};

export default StudentDashboard;
