import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script';
import './App.css';
import TeacherDashboard from './components/TeacherDashboard';
import StudentDashboard from './components/StudentDashboard';
import { initializeGoogleAuth } from './utils/auth'; // Google Auth Initialization
import { db } from './firebaseConfig'; // No need to import getFirestore anymore
import checkUserInFirestore from './utils/checkUser';  // Import the checkUser function

function App() {
  const [role, setRole] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Initialize Google Auth once when the component mounts
  useEffect(() => {
    initializeGoogleAuth();
  }, []);

  const handleGoogleLogin = () => {
    setLoading(true); 
    const authInstance = gapi.auth2.getAuthInstance();
    if (!authInstance) {
      console.error('Google Auth instance is not initialized');
      setLoading(false);
      return;
    }

    authInstance.signIn()
      .then(async (googleUser) => {
        setLoading(false);
        const profile = googleUser.getBasicProfile();
        const email = profile.getEmail();
        console.log("Logged in as:", email);

        // Call the utility function to check the user in Firestore
        checkUserInFirestore(email);  // Pass the email to check if it's in Firestore

        const userRef = doc(db, "users", email);
        const docSnap = await getDoc(userRef);

        if (docSnap.exists()) {
          const userData = docSnap.data();
          console.log("User role:", userData.role);
          setRole(userData.role);
          setIsLoggedIn(true);
        } else {
          console.log("Email not found in Firestore");
          setRole(null);
          alert('Email is not registered. Please contact the admin.');
        }
      })
      .catch((error) => {
        setLoading(false);
        setError('Login failed, please try again.');
        console.error('Google login failed', error);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Creative Assistant</h1>
        {isLoggedIn ? (
          <>
            {role === 'teacher' && <TeacherDashboard />}
            {role === 'student' && <StudentDashboard />}
            {role === null && <p>Email is not registered. Please contact the admin.</p>}
          </>
        ) : (
          <>
            {error && <p className="error">{error}</p>}
            {loading ? <p>Loading...</p> : (
              <button onClick={handleGoogleLogin}>Login with Google</button>
            )}
          </>
        )}
      </header>
      <footer className="footer">
        <p>Creative Assistant &copy; 2024</p>
      </footer>
    </div>
  );
}

export default App;
