// src/App.js
import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script'; 
import HomePage from './HomePage';
import './App.css';
import { useNavigate } from 'react-router-dom';
import { googleAuthConfig } from './authConfig'; 

function App() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [isLoginMode, setIsLoginMode] = useState(false);
  const [showSignUpOptions, setShowSignUpOptions] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const clientId = "708800444037-5lcmbdl2gdecia44o4iq3nmhr8bhpmm9.apps.googleusercontent.com"; 

  useEffect(() => {
    function start() {
      gapi.client.init({
        clientId,
        scope: 'email',
      });
    }
    gapi.load('client:auth2', start);
  }, []);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleGoogleLogin = () => {
    const authInstance = gapi.auth2.getAuthInstance();
    setLoading(true); // Set loading state to true while waiting for login

    authInstance.signIn()
      .then((googleUser) => {
        // Successful login handling
        const profile = googleUser.getBasicProfile();
        console.log('Google login successful!');
        console.log('ID: ' + profile.getId());
        console.log('Name: ' + profile.getName());
        console.log('Email: ' + profile.getEmail());

        setIsLoggedIn(true); // Set login status to true
        setLoading(false); // Reset loading state after login
      })
      .catch((error) => {
        console.error('Google login failed:', error);
        setLoading(false); // Reset loading state on error
        setError('Login failed, please try again.'); // Set error state
      });
  };


  // Effect to navigate to home when logged in
  useEffect(() => {
    if (isLoggedIn) {
      navigate('/home'); // Navigate to home if logged in
    }
  }, [isLoggedIn, navigate]); // Include navigate in the dependency array

  const handleLoginForm = (e) => {
    e.preventDefault();
    console.log('Login with email and password');
  };

  const renderSignUpForm = () => {
    if (role === 'student') {
      return (
        <form>
          <label>Course Entry Code *</label>
          <input type="text" placeholder="Enter your six-character course entry code" required />
          <label>Name *</label>
          <input type="text" placeholder="Your Name" required />
          <label>Email Address *</label>
          <input type="email" placeholder="email@example.com" required />
          <label>Student ID *</label>
          <input type="text" placeholder="Your Student ID" required />
          <button type="submit">Sign Up as Student</button>
        </form>
      );
    } else if (role === 'teacher') {
      return (
        <form>
          <label>First Name *</label>
          <input type="text" placeholder="Your First Name" required />
          <label>Last Name *</label>
          <input type="text" placeholder="Your Last Name" required />
          <label>LSU Email Address *</label>
          <input type="email" placeholder="email@lsu.edu" required />
          <button type="submit">Sign Up as Teacher</button>
        </form>
      );
    }
    return null;
  };

  const renderLoginForm = () => {
    if (isLoginMode) {
      return (
        <form onSubmit={handleLoginForm}>
          <label>Email *</label>
          <input type="email" placeholder="email@example.com" required />
          <label>Password *</label>
          <input type="password" placeholder="Enter your password" required />
          <button type="submit">Log In</button>
          <div className="login-methods">
            <p>Or log in with</p>
            <button className="google-button" onClick={handleGoogleLogin}>
              Login using Google
            </button>
          </div>
        </form>
      );
    }
    return null;
  };

  const handleSignUpClick = () => {
    setShowSignUpOptions(true);
    setIsLoginMode(false);
  };

  const handleRoleSelection = (selectedRole) => {
    setRole(selectedRole);
    setShowSignUpOptions(false);
    setIsLoginMode(false);
  };

  const closeSignUpPopup = () => {
    setShowSignUpOptions(false);
  };

  const renderHomePage = () => {
    return (
      <HomePage />
    );
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Creative Assistant</h1>
        {isLoggedIn ? (
          renderHomePage() // Show home page if logged in
        ) : (
          <>
            {error && <p className="error">{error}</p>} {/* Display error message if any */}
            {loading ? ( // Check if loading
              <p>Loading...</p> // Display loading message
            ) : (
              <>
                <div className="role-buttons">
                  <button onClick={handleSignUpClick}>Sign Up</button>
                  <button onClick={() => { setIsLoginMode(true); setRole(null); }}>Log In</button>
                </div>

                {/* Render SignUp Options */}
                {showSignUpOptions && (
                  <div className="popup-container">
                    <div className="popup">
                      <h3>Sign Up</h3>
                      <button onClick={() => handleRoleSelection('student')}>Sign Up as Student</button>
                      <button onClick={() => handleRoleSelection('teacher')}>Sign Up as Teacher</button>
                      <button onClick={closeSignUpPopup}>Cancel</button>
                    </div>
                  </div>
                )}

                {role && renderSignUpForm()}
                {isLoginMode && renderLoginForm()}
              </>
            )}
          </>
        )}
      </header>
      <footer className="footer">
        <p>Creative Assistant &copy; 2024</p>
      </footer>
    </div>
  );
}
export default App;
