import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script';
import HomePage from './components/HomePage';
import TeacherDashboard from './components/TeacherDashboard';
import StudentDashboard from './components/StudentDashboard';
import './App.css';
import { useNavigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import SignUpForm from './components/SignUpForm';
import RoleSelection from './components/RoleSelection';
import CSVUpload from './components/CSVUpload';
import { initializeGoogleAuth } from './utils/auth';

function App() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [isLoginMode, setIsLoginMode] = useState(false);
  const [showSignUpOptions, setShowSignUpOptions] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null); // Add user state here

  useEffect(() => {
    initializeGoogleAuth();
  }, []);

  // Google login handler
  const handleGoogleLogin = () => {
    setLoading(true);
    const authInstance = gapi.auth2.getAuthInstance();
    authInstance.signIn()
      .then((googleUser) => {
        setIsLoggedIn(true);
        setLoading(false);
        const profile = googleUser.getBasicProfile();
        setUser({
          id: profile.getId(),
          name: profile.getName(),
          email: profile.getEmail(),
        });

        // Fetch role from CSV upload data (stored in localStorage)
        const roleData = JSON.parse(localStorage.getItem('userRoles')) || {};
        const userRole = roleData[profile.getEmail()];

        if (userRole) {
          setRole(userRole); // Assign role if found
        } else {
          setRole(null); // Set to null if no role is found
          alert('No role assigned. Please contact the admin.');
        }
      })
      .catch((error) => {
        setLoading(false);
        setError('Login failed, please try again.');
        console.error('Google login failed', error);
      });
  };

  // Navigate based on user role
  useEffect(() => {
    if (isLoggedIn && role) {
      if (role === 'teacher') {
        navigate('/teacher-dashboard');
      } else if (role === 'student') {
        navigate('/student-dashboard');
      } else {
        navigate('/home'); // Fallback to HomePage if no role is set
      }
    }
  }, [isLoggedIn, role, navigate]);

  const handleLoginForm = (e) => {
    e.preventDefault();
    console.log('Login with email and password');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Creative Assistant</h1>
        {isLoggedIn ? (
          <>
            {role === 'teacher' && <TeacherDashboard />}
            {role === 'student' && <StudentDashboard />}
            {!role && <HomePage />}
          </>
        ) : (
          <>
            {error && <p className="error">{error}</p>}
            {loading ? <p>Loading...</p> : (
              <>
                <div className="role-buttons">
                  <button onClick={() => setShowSignUpOptions(true)}>Sign Up</button>
                  <button onClick={() => { setIsLoginMode(true); setRole(null); }}>Log In</button>
                </div>
                {showSignUpOptions && <RoleSelection setRole={setRole} setShowSignUpOptions={setShowSignUpOptions} />}
                {role && <SignUpForm role={role} />}
                {isLoginMode && <LoginForm handleLoginForm={handleLoginForm} handleGoogleLogin={handleGoogleLogin} />}
                <CSVUpload />
              </>
            )}
          </>
        )}
      </header>
      <footer className="footer">
        <p>Creative Assistant &copy; 2024</p>
      </footer>
    </div>
  );
}

export default App;
