import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { db } from '../firebaseConfig';
import { doc, getDoc } from 'firebase/firestore';
import './ClassroomDetails.css';

const ClassroomDetails = () => {
  const { classroomId } = useParams(); // Capture classroomId from URL
  const [classroom, setClassroom] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchClassroomDetails = async () => {
      try {
        // Reference to the classroom document
        const classroomRef = doc(db, 'classrooms', classroomId);
        const classroomSnap = await getDoc(classroomRef);

        if (classroomSnap.exists()) {
          console.log('Classroom Data:', classroomSnap.data()); // Log the classroom data
          setClassroom(classroomSnap.data());
        } else {
          console.log('Classroom not found');
        }
      } catch (error) {
        console.error('Error fetching classroom details:', error);
      }
      setLoading(false);
    };

    fetchClassroomDetails();
  }, [classroomId]); // Depend on classroomId to fetch data

  return (
    <div className="classroom-details">
      {loading ? (
        <p>Loading classroom details...</p>
      ) : (
        <div>
          {/* Display class ID or an error message if data is not available */}
          <h1>{classroom?.classID || 'Class not found'}</h1>
        </div>
      )}
    </div>
  );
};

export default ClassroomDetails;
