// ClassroomDetails.js
import React, { useState, useEffect } from 'react';
import { db } from '../firebaseConfig';
import { useNavigate, useParams } from 'react-router-dom';
import { collection, doc, getDoc, getDocs, setDoc, query, where } from 'firebase/firestore';
import * as XLSX from 'xlsx';
import './ClassroomDetails.css';

const ClassroomDetails = () => {
  const [classroom, setClassroom] = useState(null);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const { classroomId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClassroomDetails = async () => {
      const classroomRef = doc(db, 'classrooms', classroomId);
      const classroomSnap = await getDoc(classroomRef);

      if (classroomSnap.exists()) {
        setClassroom(classroomSnap.data());
        const studentsCollection = collection(classroomRef, 'students');
        const querySnapshot = await getDocs(studentsCollection);
        setStudents(querySnapshot.docs.map(doc => doc.data()));
      }
      setLoading(false);
    };

    fetchClassroomDetails();
  }, [classroomId]);

  const handleManageClassClick = () => {
    // Navigate to classroom management page
    navigate(`/classroom/${classroomId}/manage`);
  };

  const handleProjectsClick = () => {
    // Navigate to the projects page
    navigate(`/classroom/${classroomId}/projects`);
  };

  return (
    <div className="classroom-details">
      {loading ? (
        <p>Loading classroom details...</p>
      ) : (
        <>
          <h1>{classroom?.classID}</h1>
          <button onClick={handleManageClassClick}>Manage Classroom</button>
          <button onClick={handleProjectsClick}>Projects</button>

          {/* Display enrolled students */}
          <div className="students-list">
            <h2>Enrolled Students</h2>
            {students.length > 0 ? (
              students.map(student => (
                <p key={student.email}>{student.lastName}, {student.firstName}</p>
              ))
            ) : (
              <p>No students enrolled yet.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default ClassroomDetails;
