import { getFirestore, doc, setDoc } from 'firebase/firestore';
import { getAuth } from 'firebase/auth';

const db = getFirestore();
const auth = getAuth();

// Add user with role to Firestore
export const addUserWithRole = async (email, role) => {
  if (!email || !role) {
    throw new Error('Email and role are required');
  }

  try {
    const userRef = doc(db, 'users', email); // Use email as the document ID
    await setDoc(userRef, {
      email,
      role,
      createdAt: new Date(),
    });

    // Optionally, add additional logic for other fields like user name, etc.

    console.log(`User ${email} added with role: ${role}`);
  } catch (error) {
    console.error('Error adding user with role:', error);
    throw new Error('Error adding user to Firestore');
  }
};
