import React, { useEffect, useState } from 'react';
import { db } from '../firebaseConfig';
import { doc, getDoc } from 'firebase/firestore';

const StudentDashboard = () => {
  const [assignment, setAssignment] = useState(null);

  useEffect(() => {
    const fetchAssignment = async () => {
      const userEmail = 'jefenagracia10@gmail.com'; // Replace with the logged-in user's email
      const projectName = 'Project A'; // Or dynamically obtain project names based on user data

      const assignmentRef = doc(db, 'project', `${userEmail}-${projectName}`);
      const docSnap = await getDoc(assignmentRef);

      if (docSnap.exists()) {
        setAssignment(docSnap.data());
      } else {
        console.log("No assignment found.");
      }
    };

    fetchAssignment();
  }, []);

  return (
    <div>
      <h2>Student Dashboard</h2>
      {assignment ? (
        <div>
          <h3>Assigned Project: {assignment.projectName}</h3>
          <p>Team Name: {assignment.teamName}</p>
          <p>Assigned At: {assignment.assignedAt}</p>
        </div>
      ) : (
        <p>No assignment found.</p>
      )}
    </div>
  );
};

export default StudentDashboard;
