// src/components/CSVUpload.js
import React, { useState } from 'react';
import parseCSV from '../utils/parseCSV';

function CSVUpload({ onUpload }) {
  const [file, setFile] = useState(null);
  const [uploadError, setUploadError] = useState('');

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (file) {
      try {
        const parsedData = await parseCSV(file);
        onUpload(parsedData); // Pass parsed data to parent component (e.g., TeacherDashboard)
      } catch (error) {
        setUploadError('Failed to parse CSV file.');
      }
    } else {
      setUploadError('Please select a file to upload.');
    }
  };

  return (
    <div className="csv-upload">
      <h3>Upload CSV File for Group Assignments</h3>
      <input type="file" accept=".csv" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      {uploadError && <p className="error">{uploadError}</p>}
    </div>
  );
}

export default CSVUpload;
