import React, { useState } from 'react';
import './TeacherDashboard.css'; // Optional, for styling the modal

const TeacherDashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [courseID, setCourseID] = useState('');
  const [file, setFile] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleCourseIDChange = (e) => {
    setCourseID(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Validation check
    if (!courseID || !file) {
      setErrorMessage('Please fill in both the course ID and upload a file.');
      return;
    }

    // Process the file upload here (e.g., send the file to Firebase)
    console.log('Course ID:', courseID);
    console.log('File:', file);

    // For now, close the modal after submission
    setIsModalOpen(false);
  };

  return (
    <div className="teacher-dashboard">
      <h1>Creative Assistant</h1>
      <h2>Your Classrooms</h2>

      {/* Button to open the modal */}
      <button className="plus-button" onClick={() => setIsModalOpen(true)}>
        +
      </button>

      {/* List of classrooms (placeholder for now) */}
      <div className="classroom-list">
        <button>Classroom 1</button>
        <button>Classroom 2</button>
      </div>

      {/* Modal for creating a new classroom */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setIsModalOpen(false)}>
              &times;
            </span>
            <h2>Create New Classroom</h2>
            <form onSubmit={handleFormSubmit}>
              <div>
                <label>Course ID:</label>
                <input
                  type="text"
                  value={courseID}
                  onChange={handleCourseIDChange}
                  placeholder="Enter course ID"
                />
              </div>
              <div>
                <label>Upload Students (CSV/Excel):</label>
                <input type="file" accept=".csv, .xlsx" onChange={handleFileChange} />
              </div>
              {errorMessage && <p className="error">{errorMessage}</p>}
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherDashboard;
