import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script'; 
import HomePage from './components/HomePage';
import './App.css';
import { useNavigate } from 'react-router-dom';
import GoogleLoginButton from './components/GoogleLoginButton';
import LoginForm from './components/LoginForm';
import SignUpForm from './components/SignUpForm';
import RoleSelection from './components/RoleSelection';
import { initializeGoogleAuth } from './utils/auth'; 

function App() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [isLoginMode, setIsLoginMode] = useState(false);
  const [showSignUpOptions, setShowSignUpOptions] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    initializeGoogleAuth();
  }, []);

  const handleGoogleLogin = () => {
    setLoading(true);
    const authInstance = gapi.auth2.getAuthInstance();
    authInstance.signIn()
      .then((googleUser) => {
        setIsLoggedIn(true);
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
        setError('Login failed, please try again.');
      });
  };

  useEffect(() => {
    if (isLoggedIn) {
      navigate('/home');
    }
  }, [isLoggedIn, navigate]);

  const handleLoginForm = (e) => {
    e.preventDefault();
    console.log('Login with email and password');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Creative Assistant</h1>
        {isLoggedIn ? (
          <HomePage />
        ) : (
          <>
            {error && <p className="error">{error}</p>}
            {loading ? <p>Loading...</p> : (
              <>
                <div className="role-buttons">
                  <button onClick={() => setShowSignUpOptions(true)}>Sign Up</button>
                  <button onClick={() => { setIsLoginMode(true); setRole(null); }}>Log In</button>
                </div>
                {showSignUpOptions && (
                  <RoleSelection setRole={setRole} setShowSignUpOptions={setShowSignUpOptions} />
                )}
                {role && <SignUpForm role={role} />}
                {isLoginMode && <LoginForm handleLoginForm={handleLoginForm} handleGoogleLogin={handleGoogleLogin} />}
              </>
            )}
          </>
        )}
      </header>
      <footer className="footer">
        <p>Creative Assistant &copy; 2024</p>
      </footer>
    </div>
  );
}

export default App;
