// src/App.js
import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script';
import './App.css';
import TeacherDashboard from './components/TeacherDashboard';
import StudentDashboard from './components/StudentDashboard';
import TLDrawPage from './components/TLDrawPage';
import { initializeGoogleAuth } from './utils/auth';
import { db } from './firebaseConfig'; 
import { getFirestore, collection, query, where, getDocs } from 'firebase/firestore';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';

function App() {
  const [role, setRole] = useState(null); // Role state
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Login state
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(null); // Error state
  const navigate = useNavigate();

  useEffect(() => {
    initializeGoogleAuth();
  }, []);

  const handleGoogleLogin = () => {
    setLoading(true);
    const authInstance = gapi.auth2.getAuthInstance();

    if (!authInstance) {
      console.error('Google Auth instance is not initialized');
      setLoading(false);
      return;
    }

    authInstance.signIn()
      .then(async (googleUser) => {
        setLoading(false);
        const profile = googleUser.getBasicProfile();
        const email = profile.getEmail();
        console.log("Logged in as:", email);

        // Check Firestore for the user's role using the email field
        const usersCollection = collection(db, "users");
        const q = query(usersCollection, where("email", "==", email));
        const querySnapshot = await getDocs(q);

        if (!querySnapshot.empty) {
          querySnapshot.forEach((doc) => {
            const userData = doc.data();
            console.log("User role:", userData.role);
            setRole(userData.role);
            setIsLoggedIn(true);

            // Navigate to the appropriate dashboard based on role
            if (userData.role === 'teacher') navigate('/teacher');
            else if (userData.role === 'student') navigate('/student');
          });
        } else {
          console.log("Email not found in Firestore");
          setRole(null);
          alert('Email is not registered. Please contact the admin.');
        }
      })
      .catch((error) => {
        setLoading(false);
        setError('Login failed, please try again.');
        console.error('Google login failed', error);
      });
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Creative Assistant</h1>

          {isLoggedIn ? (
            <>
              {role === 'teacher' && <TeacherDashboard />}
              {role === 'student' && <StudentDashboard />}
              {role === null && <p>Email is not registered. Please contact the admin.</p>}
            </>
          ) : (
            <>
              {error && <p className="error">{error}</p>}
              {loading ? <p>Loading...</p> : (
                <button onClick={handleGoogleLogin}>Login with Google</button>
              )}
            </>
          )}
        </header>
        <Routes>
          {/* Define routes for dashboards and TL Draw page */}
          <Route path="/teacher" element={<TeacherDashboard />} />
          <Route path="/student" element={<StudentDashboard />} />
          <Route path="/tldraw/:projectName" element={<TLDrawPage />} />
        </Routes>
        <footer className="footer">
          <p>Creative Assistant &copy; 2024</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
