import React, { useState, useEffect } from 'react';
import { gapi } from 'gapi-script';
import { useNavigate } from 'react-router-dom';
import './App.css';
import TeacherDashboard from './components/TeacherDashboard';
import StudentDashboard from './components/StudentDashboard';
import { initializeGoogleAuth } from './utils/auth';
import { getFirestore, doc, getDoc } from "firebase/firestore"; 

function App() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);
  const db = getFirestore(); // Firebase Firestore instance

  useEffect(() => {
    initializeGoogleAuth();
  }, []);

  // Google login handler
  const handleGoogleLogin = () => {
    setLoading(true);
    const authInstance = gapi.auth2.getAuthInstance();
    authInstance.signIn()
      .then(async (googleUser) => {
        setIsLoggedIn(true);
        setLoading(false);
        const profile = googleUser.getBasicProfile();
        setUser({
          id: profile.getId(),
          name: profile.getName(),
          email: profile.getEmail(),
        });

        // Check Firestore for the user's role
        const userRef = doc(db, "users", profile.getEmail()); // Firestore document reference
        const docSnap = await getDoc(userRef);

        if (docSnap.exists()) {
          const userData = docSnap.data();
          setRole(userData.role); // Set the role based on the document
        } else {
          setRole(null); // Email not found in the database
          alert('Email is not registered. Please contact the admin.');
        }
      })
      .catch((error) => {
        setLoading(false);
        setError('Login failed, please try again.');
        console.error('Google login failed', error);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Creative Assistant</h1>
        {isLoggedIn ? (
          <>
            {/* Render the dashboard based on role */}
            {role === 'teacher' && <TeacherDashboard />}
            {role === 'student' && <StudentDashboard />}
            {role === null && <p>Email is not registered.</p>}
          </>
        ) : (
          <>
            {error && <p className="error">{error}</p>}
            {loading ? <p>Loading...</p> : (
              <>
                <button onClick={handleGoogleLogin}>Login with Google</button>
              </>
            )}
          </>
        )}
      </header>
      <footer className="footer">
        <p>Creative Assistant &copy; 2024</p>
      </footer>
    </div>
  );
}

export default App;
