import React, { useEffect, useState } from 'react';
import { db } from '../firebaseConfig';
import { collection, getDocs, doc, getDoc } from 'firebase/firestore'; // Added doc here
import { useNavigate } from 'react-router-dom';
import { gapi } from 'gapi-script';
import './StudentDashboard.css'; // Ensure the CSS file is imported

const StudentDashboard = () => {
  const [classrooms, setClassrooms] = useState([]);
  const [selectedClassroom, setSelectedClassroom] = useState(null);
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClassrooms = async () => {
      setLoading(true);
      try {
        // Get the current user's email from Google Auth
        const authInstance = gapi.auth2.getAuthInstance();
        const googleUser = authInstance.currentUser.get();
        const profile = googleUser.getBasicProfile();
        const userEmail = profile.getEmail();

        // Fetch classrooms where the student's email is present in the students subcollection
        const classroomsCollection = collection(db, 'classrooms');
        const querySnapshot = await getDocs(classroomsCollection);

        const classroomsData = [];
        for (const docSnap of querySnapshot.docs) {
          const studentsSubcollection = collection(docSnap.ref, 'students');
          const studentDoc = await getDoc(doc(studentsSubcollection, userEmail));

          // Check if user is a student
          if (studentDoc.exists()) {
            classroomsData.push({
              id: docSnap.id,
              classroomName: docSnap.data().classID, // Assume classID field stores classroom name
            });
          }
        }

        // Check if user is a teacher
        const teacherClassrooms = querySnapshot.docs.filter((docSnap) => docSnap.data().teacherEmail === userEmail);
        if (teacherClassrooms.length > 0) {
          // If a teacher is logged in, navigate them to the teacher's dashboard
          navigate('/teacher-dashboard');
        }

        if (classroomsData.length === 0) {
          setError('No classrooms found.');
        }
        setClassrooms(classroomsData);
      } catch (error) {
        console.error("Error fetching classrooms:", error);
        setError('Error fetching classrooms.');
      } finally {
        setLoading(false);
      }
    };

    fetchClassrooms();
  }, [navigate]);

  const handleClassroomClick = async (classroomId) => {
    setLoading(true);
    setSelectedClassroom(classroomId);
    setAssignments([]); // Clear previous assignments

    try {
      // Fetch assignments for the selected classroom
      const assignmentsCollection = collection(db, 'classrooms', classroomId, 'projects');
      const querySnapshot = await getDocs(assignmentsCollection);

      if (querySnapshot.empty) {
        setError('No assignments found for this classroom.');
      } else {
        const assignmentsData = querySnapshot.docs.map(doc => doc.data());
        setAssignments(assignmentsData);
      }
    } catch (error) {
      console.error("Error fetching assignments:", error);
      setError('Error fetching assignments.');
    } finally {
      setLoading(false);
    }
  };

  const openProjectPage = (projectName) => {
    navigate(`/project/${projectName}`);
  };

  // Handle logout
  const handleLogout = () => {
    const authInstance = gapi.auth2.getAuthInstance();
    authInstance.signOut().then(() => {
      navigate('/');
    });
  };

  return (
    <div className="student-dashboard">
      {/* Navigation Bar */}
      <div className="navbar">
        <h2>Creative Assistant</h2>
        <div className="nav-links">
          <button onClick={() => navigate('/')}>Home</button>
          <button onClick={() => navigate('/profile')}>Profile</button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>

      <div className="dashboard-content">
        <h3>Your Classrooms</h3>

        {loading ? (
          <p className="loading">Loading your classrooms...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : classrooms.length > 0 ? (
          <div className="classroom-list">
            {classrooms.map((classroom) => (
              <button
                key={classroom.id}
                onClick={() => handleClassroomClick(classroom.id)}
                className="classroom-button"
              >
                {classroom.classroomName}
              </button>
            ))}
          </div>
        ) : (
          <p>No classrooms found.</p>
        )}

        {selectedClassroom && assignments.length > 0 && (
          <div className="assignments-list">
            <h3>Assignments in {selectedClassroom}</h3>
            {assignments.map((assignment, index) => (
              <div key={index} className="assignment-card">
                <h4>{assignment.projectName}</h4>
                <button
                  onClick={() => openProjectPage(assignment.projectName)}
                  className="project-button"
                >
                  View Project
                </button>
                <p><strong>Due Date:</strong> {assignment.dueDate}</p>
                <p><strong>Status:</strong> {assignment.status}</p>
              </div>
            ))}
          </div>
        )}

        {selectedClassroom && assignments.length === 0 && !loading && (
          <p>No assignments found for this classroom.</p>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
