import React, { useState } from 'react';
import { storage, db } from '../firebaseConfig'; // Assuming Firebase has been configured in firebaseConfig
import { getFirestore, doc, setDoc, collection } from 'firebase/firestore';
import XLSX from 'xlsx';
import Papa from 'papaparse';

const TeacherDashboard = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
  };

  const handleFileUpload = async () => {
    if (!file) {
      setError("Please select a file to upload.");
      return;
    }
    setLoading(true);

    try {
      const reader = new FileReader();
      reader.onload = async (e) => {
        const fileContent = e.target.result;

        let data;
        if (file.name.endsWith(".xlsx")) {
          const workbook = XLSX.read(fileContent, { type: 'binary' });
          const sheetName = workbook.SheetNames[0];
          const sheet = workbook.Sheets[sheetName];
          data = XLSX.utils.sheet_to_json(sheet);
        } else if (file.name.endsWith(".csv")) {
          data = Papa.parse(fileContent, { header: true, skipEmptyLines: true }).data;
        }

        if (data) {
          // For each record, update Firestore with assignment and student details
          const batch = db.batch();
          data.forEach((item) => {
            const studentRef = doc(db, 'users', item.email);
            const assignmentRef = doc(db, 'assignments', item.email);

            // Assuming the data contains email and projectTeam
            const userData = {
              email: item.email,
              role: 'student', // Assuming role is student
            };

            const assignmentData = {
              project: item.projectTeam,
              assigned: true,
            };

            // Add user to the Firestore 'users' collection
            batch.set(studentRef, userData);

            // Add assignment to the Firestore 'assignments' collection
            batch.set(assignmentRef, assignmentData);
          });

          // Commit the batch update
          await batch.commit();
          alert("File uploaded and data saved successfully.");
        } else {
          setError("Invalid file format.");
        }
      };

      reader.readAsBinaryString(file);
    } catch (err) {
      console.error("Error uploading file: ", err);
      setError("Error uploading file. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Upload Project Teams (CSV/Excel)</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleFileUpload} disabled={loading}>
        {loading ? 'Uploading...' : 'Upload File'}
      </button>
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default TeacherDashboard;
