import React, { useEffect, useState } from 'react';
import { db } from '../firebaseConfig'; // Import Firestore instance
import { doc, getDoc } from 'firebase/firestore';

const StudentDashboard = () => {
  const [assignment, setAssignment] = useState(null);

  // Example of fetching data
  useEffect(() => {
    const fetchAssignment = async () => {
      const user = 'student-email@example.com'; // Replace with actual user email
      const assignmentRef = doc(db, 'assignments', user);
      const docSnap = await getDoc(assignmentRef);
      if (docSnap.exists()) {
        setAssignment(docSnap.data());
      }
    };

    fetchAssignment();
  }, []);

  return (
    <div>
      <h2>Student Dashboard</h2>
      {assignment ? (
        <div>
          <h3>Assigned Project: {assignment.project}</h3>
        </div>
      ) : (
        <p>No assignment found.</p>
      )}
    </div>
  );
};

export default StudentDashboard;
