// src/components/StudentDashboard.js
import React, { useState, useEffect } from 'react';

function StudentDashboard() {
  const [assignments, setAssignments] = useState([]);
  const [progress, setProgress] = useState({});

  useEffect(() => {
    // Mock data for assignments; in a real app, fetch from an API
    setAssignments([
      { id: 1, title: 'Assignment 1' },
      { id: 2, title: 'Assignment 2' },
    ]);
    setProgress({ 1: 'In Progress', 2: 'Not Started' });
  }, []);

  const updateProgress = (id, status) => {
    setProgress((prevProgress) => ({ ...prevProgress, [id]: status }));
  };

  return (
    <div className="student-dashboard">
      <h1>Welcome, Student!</h1>

      <section>
        <h2>Your Assignments</h2>
        <ul>
          {assignments.map((assignment) => (
            <li key={assignment.id}>
              <h3>{assignment.title}</h3>
              <p>Status: {progress[assignment.id]}</p>
              <button onClick={() => updateProgress(assignment.id, 'In Progress')}>
                Mark as In Progress
              </button>
              <button onClick={() => updateProgress(assignment.id, 'Completed')}>
                Mark as Completed
              </button>
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h2>Collaborate</h2>
        <p>Access your group projects here.</p>
        {/* You can add collaborative tools or links to shared projects */}
      </section>
    </div>
  );
}

export default StudentDashboard;
